DROP DATABASE IF EXISTS bolzplatz;
CREATE DATABASE bolzplatz;
USE bolzplatz;
CREATE TABLE mods (type VARCHAR(255), name VARCHAR(255), author VARCHAR(255), date DATE, file BLOB);
CREATE TABLE mods2_data (type VARCHAR(255), name VARCHAR(255), author VARCHAR(255), date DATE, file BLOB);
CREATE TABLE hof_teams (TeamID INT, Mode VARCHAR(255), TeamName VARCHAR(255), No INT, Offline BOOLEAN, LastDate DATE, LastName VARCHAR(255));
CREATE TABLE hof_teams_wc (TeamID INT, Mode VARCHAR(255), TeamName VARCHAR(255), No INT, Offline BOOLEAN, LastDate DATE, LastName VARCHAR(255));
CREATE TABLE hof_user (TeamID INT, Name VARCHAR(255),  Datum DATE, Land VARCHAR(255), Ort VARCHAR(255), SubTstamp DATE, FName VARCHAR(255), FPass VARCHAR(255), Server VARCHAR(255));
CREATE TABLE hof_user_wc (TeamID INT, Name VARCHAR(255),  Datum DATE, Land VARCHAR(255), Ort VARCHAR(255), SubTstamp DATE, FName VARCHAR(255), FPass VARCHAR(255), Server VARCHAR(255));
CREATE TABLE download (Prg VARCHAR(255), dl VARCHAR(255));
CREATE TABLE mods2_rubrik (ID INT, Bez_DE VARCHAR(255), Bez_EN VARCHAR(255), Bez_FR VARCHAR(255));
CREATE TABLE news (Datum DATE, Head_D VARCHAR(255), Text_D VARCHAR(255), Head_E VARCHAR(255), Text_E VARCHAR(255), Head_F VARCHAR(255), Text_F VARCHAR(255), Autor VARCHAR(255), Bild BLOB);
CREATE TABLE presse (Datum DATE, Head_D VARCHAR(255), Text_D VARCHAR(255), Head_E VARCHAR(255), Text_E VARCHAR(255), Head_F VARCHAR(255), Text_F VARCHAR(255), Autor VARCHAR(255), Bild BLOB);
CREATE TABLE faq (Sort VARCHAR(255), Text_d VARCHAR(255), Text_e VARCHAR(255), Text_f VARCHAR(255), Art VARCHAR(255), Antw_Sort VARCHAR(255), TFaqID INT, Date DATE, SubHead_d VARCHAR(255), SubHead_e VARCHAR(255), SubHead_f VARCHAR(255));